CREATE DEFINER=`Admin`@`%` FUNCTION `GETOVERSLA`(INSTANCEID VARCHAR(100),TYPE VARCHAR(100)) RETURNS varchar(1000) CHARSET utf8
BEGIN
DECLARE RETVAL VARCHAR(100)  DEFAULT '' ;
DECLARE STOPFLAG INT;
DECLARE CUR_SLAV CURSOR FOR SELECT IF(T1.DEAL_TIME-T1.SLA>0,1,0) FROM
    (
        SELECT
            IFNULL(IF(TYPE=1,BPI.RESPONSE_TIME,BPI.DEAL_TIME),(UNIX_TIMESTAMP(NOW())-UNIX_TIMESTAMP(BPI.CREATE_TIME))/60) AS DEAL_TIME,
            IFNULL(FUNC_GETSLAVALUE(INSTANCEID, BPI.CASE_PRIORITY, TYPE),999999999) AS SLA
        FROM BPM_PROCESS_INSTANCE BPI
        WHERE BPI.ID = INSTANCEID
    )	T1;
DECLARE CONTINUE HANDLER FOR NOT FOUND SET STOPFLAG = 1;
OPEN CUR_SLAV;
REPEAT
IF(STOPFLAG IS NULL )THEN
     FETCH CUR_SLAV INTO RETVAL;
END IF;
UNTIL STOPFLAG = 1
END REPEAT;
CLOSE CUR_SLAV;
RETURN RETVAL;
END ;;
